mapper.update:
UPDATE t_coupon_template
SET stock = stock + #{number}
WHERE id = #{couponTemplateId}
  AND shop_id = #{shopId}

UPDATE t_coupon_template
SET stock = stock - #{number}
WHERE id = #{couponTemplateId}
  AND shop_id = #{shopId}
  AND stock >= #{number}

mapper.select:
SELECT *
FROM t_coupon_template
WHERE id = #{id}
  AND shop_id = #{shopId}
  AND del_flag = 0
