mapper.update:
UPDATE t_coupon_template
SET stock = stock + #{number}
WHERE id = #{couponTemplateId}
  AND shop_id = #{shopId}

UPDATE t_coupon_template
SET stock = stock - #{number}
WHERE id = #{couponTemplateId}
  AND shop_id = #{shopId}
  AND stock >= #{number}
